def findPal2(A):
    length = len(A)

    maxx = 0
    maxxpal = ''
    for i in range(length) :
       for j in range(i+1,length+1):
           # print(A[i:j])
           if A[i:j] == A[i:j][::-1] :
               if(len(A[i:j])>maxx):
                   maxx = len(A[i:j])
                   maxxpal = A[i:j]
    print(maxxpal)
    return maxxpal

def longestPalindrome(s):
    if(len(s)==1 or len(s)==0):
        return s
    if(s == s[::-1]):
        return s
    ans = findPal2(s)

    return ans

